// bootstrapSequelize.js

const logger = require('../../log/logFacade');
const sequelizeInfra = require('../../database/sequelize/sequelizeInfra');

// //////////////////////////////////////////////////////////////////////////////
// Properties & Constants
// //////////////////////////////////////////////////////////////////////////////

const MODULE_NAME = '[bootstrapSequelize]';

// //////////////////////////////////////////////////////////////////////////////
// Public Functions
// //////////////////////////////////////////////////////////////////////////////

exports.init = (config) => {
  logger.debug(`${MODULE_NAME} init (IN) --> config: <<config>>`);

  sequelizeInfra.init(config.database.sequelize);

  logger.debug(`${MODULE_NAME} init (OUT) --> initialized`);
};
